import { Plugin } from 'obsidian';

export default class VibeAutoOrganizerPlugin extends Plugin {

	async onload() {
		console.log('Vibe Auto Organizer plugin loaded.');

		this.addCommand({
			id: 'start-automation',
			name: 'Start Auto Organization',
			callback: () => {
				console.log('Auto organization automation started!');
			}
		});
	}

	onunload() {
		console.log('Vibe Auto Organizer plugin unloaded.');
	}
}

